CREATE OR REPLACE FUNCTION disponibilitate (
    int_id_carte IN NUMBER
) RETURN NUMBER IS
    out_id_carte NUMBER(3) := NULL;
BEGIN
    SELECT
        id_carte
    INTO out_id_carte
    FROM
        carti
    WHERE
            id_carte = int_id_carte
        AND ( (
            SELECT
                COUNT(int_id_carte)
            FROM
                inchirieri
            WHERE
                    id_carte = int_id_carte
                AND data_returnare >= to_char(sysdate, 'dd-mm-YYYY')
        ) 
        < 
        (
            SELECT
                exemplare
            FROM
                carti
            WHERE
                id_carte = int_id_carte
        ));

    RETURN out_id_carte;
END;
/

